import React, { useEffect, useState } from "react";
import "./UserPage.css";
import ProductTableBody from "./ProductTableBody/ProductTableBody";
import axios from "../../utils/axios";
import { toast } from "react-toastify";
import UpdataBuyProducts from "./UpdataBuyProducts/UpdataBuyProducts";
function UserPage() {
    const [buyProducts, setBuyProducts] = useState([]);
    let updata = null;
    useEffect(() => {
        const fetching = async () => {
            try {
                const { data } = await axios.get("/product-info-buy");
                setBuyProducts(data.productInfo);
            } catch (err) {
                console.log(err);
            }
        };
        fetching();
    }, []);
    const buyProductDelete = async (id) => {
        try {
            const { data } = await axios.delete("/product-info-buy/" + id);
            toast(data.message);
        } catch (err) {
            console.log(err);
        }
    };
    const buyProductUpdate = (index) => {
        document.querySelector(".modal-body").style.display = "flex";

         updata = buyProducts[index];
    };
    return (
        <main className="user-container">
            <div className="usertable">
                <table className="table table-hover  table-striped  mx-5 table-warning">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Name</th>
                            <th scope="col">Prise</th>

                            <th scope="col">Edit</th>
                        </tr>
                    </thead>
                    <tbody>
                        {buyProducts.map((data, index) => (
                            <ProductTableBody
                                buyProductDelete={buyProductDelete}
                                buyProductUpdate={buyProductUpdate}
                                key={data._id}
                                data={data}
                                index={index}
                            />
                        ))}
                    </tbody>
                </table>
            </div>
            <UpdataBuyProducts data={updata}/>
        </main>
    );
}

export default UserPage;
