import React from "react";
import "./Navbar.css";
import { NavLink } from "react-router-dom";

export default function Navbar() {
    const activeStyles = {
        color: "#c76e1af3",
    };
    const hendlerLogout =()=>{
        const valid = window.confirm("Do you really want to log out?");
        if(valid){
            window.localStorage
        }
    }
    return (
        <nav className="navbar">
            <div className="menu">
                <button className="menu-button">
                    <i className="fa-solid fa-bars"></i>
                </button>
                <ul className="menu-list">
                    <NavLink
                        className="menu-list-item"
                        to={"/"}
                        style={({ isActive }) =>
                            isActive ? activeStyles : undefined
                        }
                    >
                        <li c> Home</li>
                    </NavLink>
                    <NavLink
                        to={"/products"}
                        className="menu-list-item"
                        style={({ isActive }) =>
                            isActive ? activeStyles : undefined
                        }
                    >
                        Products
                    </NavLink>
                    <NavLink className="menu-list-item" onClick={()=>{document.querySelector('.modal-body').style.display='flex'}}>Register</NavLink>
                </ul>
            </div>
            <div className="user-control">
                <ul className="user-control-list">
                    <NavLink
                        to={"/user"}
                        className="user-control-list-item menu-list-item"
                        style={({ isActive }) =>
                            isActive ? activeStyles : undefined
                        }
                    >
                        <i className="fa-solid fa-cart-shopping"></i>
                    </NavLink>
                    <NavLink
                        className="user-control-list-item menu-list-item"
                       
                    >
                        <i className="fa-sharp fa-solid fa-right-from-bracket" onClick={hendlerLogout} ></i>
                        
                       
                    </NavLink>
                </ul>
            </div>
        </nav>
    );
}
