import React, { useState, useEffect } from "react";
import "./Navbar.css";
import { NavLink } from "react-router-dom";
import axios from "../../utils/axios";

export default function Navbar() {
    const activeStyles = {
        color: "#c76e1af3",
    };
    const [token, setToken] = useState(false);
    const [userData, setUserDate] = useState({
        email: "nov",
        name: "a",
        _id:"63bee63ee20413544c3838ac"
    });

    setInterval(() => {
        setToken(window.localStorage.getItem("token") || false);
    }, 1000);

    const hendlerLogout = () => {
        const valid = window.confirm("Do you really want to log out?");
        if (valid) {
            window.localStorage.clear();
        }
    };
    useEffect(() => {
        const fetching = async () => {
            try {
                const { data } = await axios.get("/users/profile");
                console.log(data);
                setUserDate(data.user);
            } catch (err) {
                console.log(err);
            }
            
        };
        fetching();
    }, []);

    return (
        <nav className="navbar">
            <div className="menu">
                <button className="menu-button">
                    <i className="fa-solid fa-bars"></i>
                </button>
                <ul className="menu-list">
                    <NavLink
                        className="menu-list-item"
                        to={"/"}
                        style={({ isActive }) =>
                            isActive ? activeStyles : undefined
                        }
                    >
                        <li> Home</li>
                    </NavLink>
                    <NavLink
                        to={"/products"}
                        className="menu-list-item"
                        style={({ isActive }) =>
                            isActive ? activeStyles : undefined
                        }
                    >
                        Products
                    </NavLink>
                    <NavLink
                        className="menu-list-item"
                        onClick={() => {
                            document.querySelector(
                                ".modal-body"
                            ).style.display = "flex";
                        }}
                    >
                        Register
                    </NavLink>
                </ul>
            </div>
            <div className="user-control">
                {token ? (
                    <ul className="user-control-list">
                        <NavLink
                            to={"/user"}
                            className="user-control-list-item menu-list-item"
                            style={({ isActive }) =>
                                isActive ? activeStyles : undefined
                            }
                        >
                            <i className="fa-solid fa-cart-shopping"></i>
                        </NavLink>
                        <NavLink className="user-control-list-item menu-list-item">
                            <i className="fa-solid fa-user"></i>
                        </NavLink>
                        <ul className="user-menu">
                            <li className="user-menu-item">
                                <i className="fa-sharp fa-solid fa-circle-info"></i>{" "}
                                User Info
                            </li>
                            <li className="user-menu-item">
                                <i
                                    className="fa-sharp fa-solid fa-right-from-bracket"
                                    onClick={hendlerLogout}
                                ></i>{" "}
                                Logout
                            </li>
                        </ul>
                    </ul>
                ) : (
                    <ul className="user-control-list">
                        <NavLink
                            to={"/"}
                            className="user-control-list-item menu-list-item"
                        >
                            Login
                        </NavLink>
                    </ul>
                )}
            </div>
        </nav>
    );
}
