import React,{useState} from 'react';
import { toast } from 'react-toastify';
import axios from '../../../utils/a';

function UpdataBuyProducts({data}) {
    const rexisterClose = (e) => {
        if (
            e.target.matches(".update-modal-body") ||
            e.target.matches(".fa-rectangle-xmark")
        ) {
            document.querySelector(".update-modal-body").style.display = "none";
        }
    };
    const [updata, setUpdata] = useState(data);
    const changeHandlerUpdata = (e) => {
        const { name, value } = e.target;
        setUpdata((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const submitUpdata = async (e) => {
        e.preventDefault();
        try {
            const { data } = await axios.put("/product-info-buy/"+updata._id, updata);
            document.querySelector(".modal-body").style.display = "none";
            toast(data.message);
           
            
        } catch (err) {
            console.log(err);
        }
    };
  return (
    <div className="update-modal-body" onClick={rexisterClose}>
    <div className="modal-fixsed">
        <form className="register-form">
            <div className="register-header">
                <h2 className="register-title">Update Product</h2>
                <i className="fa-regular fa-rectangle-xmark"></i>
            </div>

            <label htmlFor="name">New name</label>
            <input
                type="text"
                value={updata.name}
                onChange={changeHandlerUpdata}
                name="name"
                id="name"
                placeholder="Your Name"
            />

            <label htmlFor="price">New price</label>
            <input
                type="text"
                value={updata.email}
                onChange={changeHandlerUpdata}
                name="price"
                id="price"
                placeholder="Your Login"
            />


            <button
                type="submit"
                onClick={submitUpdata}
                className="register-submit"
            >
                Register
            </button>
        </form>
    </div>
</div>
  )
}

export default UpdataBuyProducts
